import { db,storageRef } from "../firebase";
import { collection,getDocs,getDoc,doc } from "firebase/firestore";
import {ref as sRef,list} from "firebase/storage"

const dataRef = collection(db,"products") ;
const imgRef = sRef(storageRef,"Boards");

class DataService {

    getAllProducts =() =>{
        return getDocs(dataRef)
    }

    getProduct =(id)=>{
        const productDoc = doc(db,"products",id);
        return getDoc(productDoc);
    }
    getImages =()=>{
        const fileName = '7-INTEL-DONOR.png';
        const spaceRef = sRef(imgRef, fileName);
        return list(spaceRef)
    }
}

export default new DataService();
